sys_element_mapping.value:
{
  "model": "llama-3.3-70b-versatile",
  "messages": [
    {
      "role": "system",
      "content": "You are a sentiment analysis expert. Analyze the sentiment of the given text and respond ONLY with a valid JSON object in this exact format with no additional text: {\"sentiment\": \"positive\", \"confidence\": 0.95, \"positive_score\": 0.95, \"negative_score\": 0.03, \"neutral_score\": 0.02}. The sentiment must be one of: positive, negative, or neutral. All scores must be decimals between 0 and 1, and should add up to approximately 1.0."
    },
    {
      "role": "user",
      "content": "Analyze the sentiment of this text: {{action.text_to_analyze}}"
    }
  ],
  "temperature": 0.1,
  "max_tokens": 150,
  "response_format": {
    "type": "json_object"
  }
}

sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // Parse the Groq API response
        var responseBody = JSON.parse(inputs.response_body);
        
        // Check if choices exists
        if (!responseBody.choices || !responseBody.choices[0]) {
            throw new Error('Invalid API response structure');
        }
        
        // Extract the content from Groq's response structure
        var content = responseBody.choices[0].message.content;
        
        // Parse the sentiment JSON from the content
        var sentimentData = JSON.parse(content);
        
        // Set outputs with the sentiment data
        outputs.sentiment = sentimentData.sentiment || 'unknown';
        outputs.confidence_score = parseFloat(sentimentData.confidence) || 0;
        outputs.positive_score = parseFloat(sentimentData.positive_score) || 0;
        outputs.negative_score = parseFloat(sentimentData.negative_score) || 0;
        outputs.neutral_score = parseFloat(sentimentData.neutral_score) || 0;
        outputs.status = 'success';
        outputs.error_message = '';
        
    } catch (error) {
        // Handle any errors
        outputs.status = 'error';
        outputs.error_message = 'Failed to parse sentiment: ' + error.message;
        outputs.sentiment = 'unknown';
        outputs.confidence_score = 0;
        outputs.positive_score = 0;
        outputs.negative_score = 0;
        outputs.neutral_score = 0;
    }
})(inputs, outputs);

